    {{-- CTA Form Script --}}
    <script>
        document.getElementById('ctaEmailForm').addEventListener('submit', async function (e) {
            e.preventDefault();
            e.stopImmediatePropagation();

            const emailInput = document.getElementById('ctaEmail');
            const email = emailInput.value.trim();
            const btn = document.getElementById('ctaSubmitBtn');
            const arrowIcon = document.getElementById('ctaArrowIcon');
            const spinner = document.getElementById('ctaSpinner');
            const btnText = document.getElementById('ctaBtnText');

            if (!email) {
                Swal.fire({
                    toast: true,
                    position: 'top-end',
                    icon: 'warning',
                    title: 'Harap masukkan email!',
                    showConfirmButton: false,
                    timer: 2000
                });
                return;
            }

            // Disable button and show loading
            btn.disabled = true;
            arrowIcon.classList.add('hidden');
            spinner.classList.remove('hidden');
            btnText.textContent = 'Memproses...';

            try {
                // Send to n8n via backend
                const response = await fetch('{{ route("leads.subscribe-cta") }}', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'X-CSRF-TOKEN': document.querySelector('input[name="_token"]').value,
                        'Accept': 'application/json'
                    },
                    body: JSON.stringify({ email: email })
                });

                const data = await response.json();

                if (data.success) {
                    // Show success toast if SweetAlert2 is available
                    if (typeof Swal !== 'undefined') {
                        Swal.fire({
                            toast: true,
                            position: 'top-end',
                            icon: 'success',
                            title: 'Email terdaftar! Mengarahkan...',
                            showConfirmButton: false,
                            timer: 1500
                        });
                    }

                    // Redirect to register with email
                    setTimeout(() => {
                        window.location.href = '{{ route("register") }}';
                    }, 1000);
                } else {
                    throw new Error(data.message || 'Gagal mendaftar');
                }
            } catch (error) {
                console.error('CTA Error:', error);

                // Reset button state
                btn.disabled = false;
                arrowIcon.classList.remove('hidden');
                spinner.classList.add('hidden');
                btnText.textContent = 'Mulai';

                // Show error alert
                if (typeof Swal !== 'undefined') {
                    Swal.fire({
                        toast: true,
                        position: 'top-end',
                        icon: 'error',
                        title: error.message || 'Terjadi kesalahan, coba lagi nanti.',
                        showConfirmButton: false,
                        timer: 3000
                    });
                }
            }
        });
    </script>

    {{-- Lembaga Only Alert Script --}}
